CREATE TABLE CARRERA (ID INTEGER AUTO_INCREMENT NOT NULL, DESCRIPCION VARCHAR(255), DISTANCIA FLOAT, ESTADO VARCHAR(255), FECHA DATE, FECHA_CREACION DATE, NOMBRE VARCHAR(255), PRIMARY KEY (ID))
CREATE TABLE GRUPO (ID INTEGER AUTO_INCREMENT NOT NULL, FECHA_CREACION DATE, NOMBRE VARCHAR(255), ID_CARRERA INTEGER, PRIMARY KEY (ID))
CREATE TABLE USUARIO (ID INTEGER AUTO_INCREMENT NOT NULL, APELLIDO_1 VARCHAR(255), APELLIDO_2 VARCHAR(255), CORREO VARCHAR(255), DIRECCION VARCHAR(255), FECHA_CREACION DATE, FECHA_NAC DATE, LOCALIDAD VARCHAR(255), NOMBRE VARCHAR(255), PASS VARCHAR(255), PROVINCIA VARCHAR(255), PRIMARY KEY (ID))
CREATE TABLE AMISTAD (ESTADO VARCHAR(255), FECHA DATE, USUARIO_ID INTEGER NOT NULL, AMIGO_ID INTEGER NOT NULL, PRIMARY KEY (USUARIO_ID, AMIGO_ID))
ALTER TABLE GRUPO ADD CONSTRAINT FK_GRUPO_ID_CARRERA FOREIGN KEY (ID_CARRERA) REFERENCES CARRERA (ID)
ALTER TABLE AMISTAD ADD CONSTRAINT FK_AMISTAD_AMIGO_ID FOREIGN KEY (AMIGO_ID) REFERENCES USUARIO (ID)
ALTER TABLE AMISTAD ADD CONSTRAINT FK_AMISTAD_USUARIO_ID FOREIGN KEY (USUARIO_ID) REFERENCES USUARIO (ID)
